import { useEffect, useState } from 'react'
import { connect } from 'react-redux'
import { requestTables } from '../redux/tables-reducers'

const Tables = ({requestTables, tables, isFetching}) => {
    const [page, setPage] = useState(0)

    useEffect(() => {
        requestTables(page)
    }, [])

  return (
    <div>
        {isFetching ? <p>Loading...</p> : 
                <table>

                    <tr>
                        <th>Name</th>
                        <th>Amount</th> 
                        <th>Distance</th>
                        <th>Date</th>
                    </tr>
                    {tables.map(el => (
                        <tr key={el.id}>
                            <td>{el.name}</td>
                            <td>{el.amount}</td> 
                            <td>{el.distance}</td> 
                            <td>{el.data}</td> 
                        </tr>
                    ))}
                </table>
        }
        <div className="pagination-wrapper">
            <ul>
                <li onClick={() => setPage(0)}>1</li>
                <li onClick={() => setPage(1)}>2</li>
                <li onClick={() => setPage(2)}>3</li>
            </ul>
        </div>
    </div>
  )
}

const mapStateToProps = (state) => ({
    tables: state.tables.tables,
    isFetching: state.tables.isFetching
})

export const TablesContainer = connect(mapStateToProps, {requestTables})(Tables)
