import { useCallback, useState } from 'react'
import { connect } from 'react-redux';
import { requestTables } from '../../redux/tables-reducers'

const Filter = ({requestTables, page}) => {
    const [column, setColumn] = useState('name')
    const [condition, setCondition] = useState('=')
    const [value, setValue] = useState('')

    useEff(() => {
        requestTables(page, column, condition, value)
    }, [column, condition, value])

  return (
    <div>
        <select value={column} onChange={(event) => setColumn(event.target.value)}>
            <option value="name">name</option>
            <option value="amount">amount</option>
            <option value="distance">distance</option>
        </select>
        <select value={condition} onChange={(event) => setCondition(event.target.value)}>
            <option value="=">Равно</option>
            <option value="like">Содержит</option>
            <option value=">">Больше</option>
            <option value="<">Меньше</option>
        </select>
        <input  type="text" 
                placeholder='value' 
                value={value}
                onChange={(event) => setValue(event.target.value)}/>
    </div>
  )
}

export const FilterContainer = connect(null, {requestTables})(Filter)
