import { tablesAPI } from "../api"

// consts
const TOGGLE_IS_FETCHING = 'TOGGLE_IS_FETCHING'
const SET_TABLES = 'SET_TABLES'

let initialState = {
    tables: [],
    isFetching: false,

}

// reducers
export const tablesReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_TABLES: 
            return {...state, tables: action.tables}
        case TOGGLE_IS_FETCHING: 
            return {...state, isFetching: action.isFetching}
        default: 
            return state
    }
}

// dispatches
const setTables = (tables) => ({ type: SET_TABLES, tables })
const toggleIsFetching = (isFetching) => ({ type: TOGGLE_IS_FETCHING, isFetching })

export const requestTables = (page) => async (dispatch) => {
    dispatch(toggleIsFetching(true))
    const response = await tablesAPI.getTables(page)
    if(response.status == 200){
        dispatch(setTables(response.data))
        dispatch(toggleIsFetching(false))
    }else{
        console.log(response);
        dispatch(toggleIsFetching(false))
    }
}
