import axios from "axios";

const instance = axios.create({
    baseURL: 'http://localhost:8000'// base url
})

export const tablesAPI = {
    getTables(page, column, condition, value) {//get all rows from api
        return instance.get(`/?page=${page}&`)
    }
}
