import { useEffect, useState } from 'react'
import { connect } from 'react-redux'
import { requestTables } from '../redux/tables-reducers'

const Tables = ({requestTables, tables, pages, isFetching}) => {
    const [page, setPage] = useState(0)

    useEffect(() => {
        requestTables(page)
    }, [page])

    let pagesArr = [];
    for (let i = 1; i <= pages; i++) {
        pagesArr.push(i);
    }

  return (
    <div>
        {isFetching ? <p>Loading...</p> : 
        <>
                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Amount</th> 
                            <th>Distance</th>
                            <th>Date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {tables.map(el => (
                            <tr key={el.id}>
                                <td>{el.name}</td>
                                <td>{el.amount}</td> 
                                <td>{el.distance}</td> 
                                <td>{el.data}</td> 
                            </tr>
                        ))}
                    </tbody>
                </table>

            </>
        }

    </div>
  )
}

const mapStateToProps = (state) => ({
    tables: state.tables.tables,
    pages: state.tables.pages,
    isFetching: state.tables.isFetching
})

export const TablesContainer = connect(mapStateToProps, {requestTables})(Tables)
